import {
  GENERALINFORMATION_GET, GENERALINFORMATION_GET_SUCCEED, GENERALINFORMATION_GET_FAILED
} from '../constants/actionTypes';

const initialState = {
  informationData: [],
  name: '',
  position: ''
};

export default function generalInformation(state = initialState, action) {
  switch (action.type) {
    case GENERALINFORMATION_GET:
      return {
        ...state,
        informationData: [],
        loading: true,
        error: false,
      };
    case GENERALINFORMATION_GET_SUCCEED:
      return {
        ...state,
        informationData: action.informationData,
        name: action.name,
        position: action.position,
        loading: false,
        error: false,
      };
    case GENERALINFORMATION_GET_FAILED:
      return {
        ...state,
        loading: false,
        error: true,
      };
    default:
      return state;
  }
}
