import {
  SOCIALLINKS_GET, SOCIALLINKS_GET_SUCCEED, SOCIALLINKS_GET_FAILED
} from '../constants/actionTypes';

const initialState = {
  github: null,
  twitter: null,
  loading: true,
  error: false
};

export default function generalInformation(state = initialState, action) {
  switch (action.type) {
    case SOCIALLINKS_GET:
      return {
        ...state,
        loading: true,
        error: false,
      };
    case SOCIALLINKS_GET_SUCCEED:
      return {
        ...state,
        github: action.github,
        twitter: action.twitter,
        loading: false,
        error: false,
      };
    case SOCIALLINKS_GET_FAILED:
      return {
        ...state,
        loading: false,
        error: true,
      };
    default:
      return state;
  }
}
