import React, { Component } from 'react';
import { connect } from 'react-redux';
import Card from '../../components/card';
import Avatar from '../../components/avatar';
import PropTypes from 'prop-types';
import GeneralInformationRow from '../../components/generalInformationRow';
import Spinner from '../../components/spinner';
import { ThemeConsumer } from '../../components/themeContext';

import './style.scss';

class GeneralInformation extends Component {
  constructor(props) {
    super(props);
    this.state = { };
  }

  async componentDidMount() {
    await this.props.generalInformationGetAction();
  }

  render() {
    const { informationData, name, position, loading, avatar } = this.props;

    return (
      <ThemeConsumer>
        {
          ({ darkTheme }) => {
            const theme = darkTheme ? 'dark' : 'light';
            return (
              <Card>
                <div className="general-information-card" theme={theme}>
                  { loading ? <Spinner/> :
                  <>
                    <Avatar url={avatar}/>
                      <div className="general-information">
                        <div className="general-information-data">
                          <p className="hello" theme={theme}>Hello & Welcome</p>
                          <p className="whoami" theme={theme}>I'm <span>{name}</span></p>
                          <p className="work-position" theme={theme}>{position}</p>
                          <table>
                            {
                              Object.keys(informationData).map((name) => (<GeneralInformationRow key={name} rowName={name} rowValue={informationData[name]} />))
                            }
                          </table>
                        </div>
                      </div>
                    </>
                  }
                </div>
              </Card>
            )
          }
        }
      </ThemeConsumer>
    )
  }
};

const mapStateToProps = ({ generalInformation }) => ({
  informationData: generalInformation.informationData,
  name: generalInformation.name,
  position: generalInformation.position,
  avatar: generalInformation.avatar,
  loading: generalInformation.loading,
  error: generalInformation.error
});

export default connect(mapStateToProps, ({ generalInformation }) => ({
  generalInformationGetAction: generalInformation.get
}))(GeneralInformation);

GeneralInformation.propTypes = {
  generalInformationGetAction: PropTypes.func,
  informationData: PropTypes.object,
  name: PropTypes.string,
  position: PropTypes.string,
  avatar: PropTypes.string,
  loading: PropTypes.bool,
  error: PropTypes.bool
};
