import {
  SKILLS_GET, SKILLS_GET_SUCCEED, SKILLS_GET_FAILED
} from '../constants/actionTypes';

const initialState = {
  skills: [],
  text: null
};

export default function generalInformation(state = initialState, action) {
  switch (action.type) {
    case SKILLS_GET:
      return {
        ...state,
        skills: [],
        text: null,
        loading: true,
        error: false,
      };
    case SKILLS_GET_SUCCEED:
      return {
        ...state,
        skills: action.skills,
        text: action.text,
        loading: false,
        error: false,
      };
    case SKILLS_GET_FAILED:
      return {
        ...state,
        loading: false,
        error: true,
      };
    default:
      return state;
  }
}
