import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import SkillProgress from '../../components/skillProgress';
import Card from '../../components/card';
import Spinner from '../../components/spinner';
import { ThemeConsumer } from '../../components/themeContext';

import './style.scss';

class Main extends Component {
  async componentDidMount() {
    await this.props.skillsGetAction();
  }

  render() {
    const { skillProgressData, text, loading, error } = this.props;

    return (
      <ThemeConsumer>
        {
          ({ darkTheme }) => {
            theme = darkTheme ? "dark" : "light";

            return (
              <Card>
                <div className="skills-card" theme={theme}>
                  { loading ? <Spinner/> :
                    <>
                      <div className="skills-card-description" theme={theme}>
                      <p>{text}</p>
                      </div>
                      <div className="skills-progress">
                        {
                          skillProgressData.map(({ name, percent }) => (<SkillProgress key={name} name={name} percent={percent}/>))
                        }
                      </div>
                    </>
                  }
                </div>
              </Card>
            )
          }
        }
      </ThemeConsumer>
    )
  }
};

const mapStateToProps = ({ skills }) => ({
  skillProgressData: skills.skills,
  text: skills.text,
  loading: skills.loading,
  error: skills.error
});

export default connect(mapStateToProps, ({ skills }) => ({
  skillsGetAction: skills.get
}))(Main);

Main.propTypes = {
  skills: PropTypes.func,
  informationData: PropTypes.array,
  text: PropTypes.string,
  loading: PropTypes.bool,
  error: PropTypes.string
};
