import React, { Component } from 'react';
import {
  BrowserRouter, Route, Switch, Redirect,
} from 'react-router-dom';
import Header from '../../containers/header';
import Main from '../main';
import LoginPage from '../../containers/loginPage';
import GetToken from '../../containers/getToken';
import { ThemeProvider, themes } from '../themeContext';

import './style.scss';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isTokenPresented: localStorage.getItem('isTokenPresented') === 'true',
      theme: themes.light
    };
  }

  async componentDidMount() {
    this.setState({
      isTokenPresented: localStorage.getItem('isTokenPresented') === 'true'
    });
  }

  toggleTheme(){
    this.setState(state => ({
      theme: state.theme === themes.dark ? themes.light : themes.dark
    }));
  }

  render() {
    const Resume = () => (
      <ThemeProvider value={{
        theme: this.state.theme,
        toggleTheme: this.toggleTheme
      }}>
        <Header/>
        <Main />
      </ThemeProvider>
    );

    const LoginRoute = () => {
      const isTokenPresented = localStorage.getItem('isTokenPresented') === 'true';
      return (
        <Route render={() => (
          !isTokenPresented
            ? <Route path='/login' component={LoginPage}/>
            : <Redirect to='/resume'/>
          )}
        />
      )
    };

    const ResumeRoute = ({ component: Component, ...rest }) => {
      const isTokenPresented = localStorage.getItem('isTokenPresented') === 'true';
      return (
        <Route {...rest} render={() => (
          isTokenPresented
            ? <Route path='/resume' component={Resume}/>
            : <Redirect to='/login'/>
        )}
        />
      )
    };

    const PrivateRoute = () => {
      const isTokenPresented = localStorage.getItem('isTokenPresented') === 'true';
      return (
        <Route render={() => (
          isTokenPresented
            ? <Redirect to='/resume'/>
            : <Redirect to='/login'/>
        )}
        />
      )
    };

    return (
      <BrowserRouter>
        <Switch>
          <Route path='/token' component={GetToken}/>
          <LoginRoute path='/login'/>
          <ResumeRoute path='/resume'/>
          <PrivateRoute/>
        </Switch>
      </BrowserRouter>
    )
  }
};

export default App;
