import * as types from '../constants/actionTypes';
import { generalInformationGet } from '../services/backend';

export const generalInformationGetAction = () => async (dispatch) => {
  dispatch({
    type: types.GENERALINFORMATION_GET
  });

  try {
    const { data, name, position } = await generalInformationGet();
    return dispatch({
      type: types.GENERALINFORMATION_GET_SUCCEED,
      informationData: data,
      name,
      position
    });
  } catch(err) {
    return dispatch({
      type: types.GENERALINFORMATION_GET_FAILED
    });
  }
};
