import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import EducationContent from '../../components/educationContent';
import Spinner from '../../components/spinner';
import { ThemeConsumer } from '../../components/themeContext';

import './style.scss';

class Education extends Component {
  async componentDidMount() {
    await this.props.educationGetAction();
  }

  render() {
    const { data, loading } = this.props;

    let educationCardRight = JSON.parse(JSON.stringify(data));

    const leftLength = Math.ceil(data.length / 2);
    const educationCardLeft = [];
    for (let i = 0; i < leftLength; i++) educationCardLeft.push(educationCardRight.shift());

    return (
      <ThemeConsumer>
        {
          ({ darkTheme }) => {
            const theme = darkTheme ? "dark" : "light";

            return (
              <div className="education-layout">
                { loading ? <Spinner/> :
                  <>
                    <EducationContent educations={educationCardLeft} position="left"/>
                    <div className="vertical-line" theme={theme}/>
                    <EducationContent educations={educationCardRight} position="right"/>
                  </>
                }
              </div>
            )
          }
        }
      </ThemeConsumer>
    )
  }
};

const mapStateToProps = ({ education }) => ({
  data: education.data,
  loading: education.loading,
  error: education.error
});

export default connect(mapStateToProps, ({ education }) => ({
  educationGetAction: education.get
}))(Education);

Education.propTypes = {
  educationGetAction: PropTypes.func,
  data: PropTypes.array,
  loading: PropTypes.bool,
  error: PropTypes.bool
};
