import * as types from '../constants/actionTypes';
import { userInfo } from '../services/backend';

export const socialLinksGetAction = () => async (dispatch) => {
  dispatch({
    type: types.SOCIALLINKS_GET
  });

  try {
    const { data } = await userInfo();

    return dispatch({
      type: types.SOCIALLINKS_GET_SUCCEED,
      github: data.html_url,
      twitter: data.twitter_username ? `https://twitter.com/${data.twitter_username}` : null,
      name: data.name,
      company: data.company
    });
  } catch(err) {
    return dispatch({
      type: types.SOCIALLINKS_GET_FAILED
    });
  }
};
