import React from 'react';

const themes = {
  light: 'light',
  dark: 'dark'
}

const toggleTheme = (theme) => (theme === themes.dark ? themes.light : themes.dark);

const {
  Provider: ThemeProvider,
  Consumer: ThemeConsumer
} = React.createContext({
  theme: themes.light,
  toggleTheme
});

export {
  ThemeProvider,
  ThemeConsumer,
  themes,
  toggleTheme
};
