import React, { Component } from 'react';
import {
  BrowserRouter, Route, Switch, Redirect,
} from 'react-router-dom';
import Header from '../../containers/header';
import Main from '../main';
import LoginPage from '../../containers/loginPage'

import './style.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isTokenPresented: localStorage.getItem('isLoggedIn') === 'true',
      // isTokenChecked: false,

      // error: null,
    };
  }

  render() {
    const { isTokenPresented } = this.state;

    const LoginRoute = () => (
      <Route render={() => (
        !isTokenPresented
          ? <Route path='/login' component={LoginPage}/>
          : <Redirect to='/resume'/>
        )}
      />
    );

    const Resume = (
      <>
        <Header/>
        <Main />
      </>
    )

    return (
      <>
        <Header/>
        <Main />
      </>
    )

    // return (
    //   <BrowserRouter>
    //     <Switch>
    //       {
    //         true
    //           ? <Route path='/resume' component={Resume} />
    //           : <Route path='/login' component={LoginPage}/>
    //       }
    //       <Redirect to='/resume'/>
    //     </Switch>
    //   </BrowserRouter>
    // )

  }
};

export default App;
