import * as types from '../constants/actionTypes';
import { userInfo } from '../services/backend';

export const generalInformationGetAction = () => async (dispatch) => {
  dispatch({
    type: types.GENERALINFORMATION_GET
  });

  try {
    const { data } = await userInfo();

    return dispatch({
      type: types.GENERALINFORMATION_GET_SUCCEED,
      informationData: {
        company: data.company,
        blog: data.blog,
        location: data.location,
        email: data.email,
        bio: data.bio
      },
      name: data.name,
      position: data.company,
      avatar: data.avatar_url
    });
  } catch(err) {
    return dispatch({
      type: types.GENERALINFORMATION_GET_FAILED
    });
  }
};
