import { getAccessToken } from '../services/backend';

export default {
  state: {
    loading: true,
    error: null
  },

  reducers: {
    fetching: (state, payload) => ({
      loading: true,
      error: null 
    }),
    succeed: (state, payload) => ({
      ...state,
      loading: false
    }), 
    failed: (state, error) => ({
      ...state,
      loading: false,
      error
    }) 
  },

  effects: {
    async tokenGet(payload, rootState) {
      try {
        this.fetching();
        await getAccessToken(payload);
      } catch(err) {
        this.failed(err);
      }
    }
  }
}
