import * as types from '../constants/actionTypes';
import { skillsGet } from '../services/backend';

export const skillsGetAction = () => async (dispatch) => {
  dispatch({
    type: types.SKILLS_GET
  });

  try {
    const { data } = await skillsGet();

    return dispatch({
      type: types.SKILLS_GET_SUCCEED,
      skills: data.skills,
      text: data.text
    });
  } catch(err) {
    return dispatch({
      type: types.SKILLS_GET_FAILED
    });
  }
};
