import React, { Component } from 'react';
import { connect } from 'react-redux';
import { generalInformationGetAction } from '../../actions/generalInformation';
import Card from '../../components/card';
import Avatar from '../../components/avatar';
import PropTypes from 'prop-types';
import GeneralInformationRow from '../../components/generalInformationRow';

import './style.css';

class GeneralInformation extends Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     loading: true,
  //     error: false
  //   };
  // }

  async componentDidMount() {
    await this.props.generalInformationGetAction();
  }

  render() {
    const { informationData, name, position, loading, error } = this.props;

    // informationData.map(() => {
    //   console.log("111")
    // })
    console.log(informationData)

    return (
      <Card>
        <div className="general-information-card">
        <Avatar/>
          <div className="general-information">
            <div className="general-information-data">
              <p className="hello">Hello & Welcome</p>
              <p className="whoami">I'm <span>{name}</span></p>
              <p className="work-position">{position}</p>
              <table>
                {
                   loading && informationData.map(({ name, value }) => (<GeneralInformationRow key={name} rowName={name} rowValue={value} />))
                }
              </table>
            </div>
          </div>
        </div>
      </Card>
    )
  }
};

const mapStateToProps = ({ generalInformation }) => ({
  informationData: generalInformation.informationData,
  name: generalInformation.name,
  position: generalInformation.position,
  loading: generalInformation.loading,
  error: generalInformation.error
});

export default connect(mapStateToProps, {
  generalInformationGetAction
})(GeneralInformation);

GeneralInformation.propTypes = {
  generalInformationGetAction: PropTypes.func,
  informationData: PropTypes.array,
  name: PropTypes.string,
  position: PropTypes.string,
  loading: PropTypes.bool,
  error: PropTypes.bool
};
