import {
  EDUCATION_GET, EDUCATION_GET_SUCCEED, EDUCATION_GET_FAILED
} from '../constants/actionTypes';

const initialState = {
  data: [],
  text: null
};

export default function generalInformation(state = initialState, action) {
  switch (action.type) {
    case EDUCATION_GET:
      return {
        ...state,
        data: [],
        loading: true,
        error: false,
      };
    case EDUCATION_GET_SUCCEED:
      return {
        ...state,
        data: action.data,
        loading: false,
        error: false,
      };
    case EDUCATION_GET_FAILED:
      return {
        ...state,
        loading: false,
        error: true,
      };
    default:
      return state;
  }
}
