import React, { Component }  from 'react';
import SocialLinkIcon from '../../components/socialLinkIcon';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Spinner from '../../components/spinner';
import { ThemeConsumer } from '../../components/themeContext';

import './style.scss';

class Header extends Component {

  async componentDidMount() {
    await Promise.all([
      this.props.skillsGetAction(),
      this.props.educationGetAction(),
      this.props.generalInformationGetAction()
    ]);
  }

  render() {
    const { socialLinkData, loading, name, company } = this.props;

    return (
      <ThemeConsumer>
        { ({ darkTheme, toggleTheme }) => {
            const theme = darkTheme ? "dark" : "light";

            return (
              <header theme={theme}>
                <div className="header-content-data">
                  <h1 className="person-name">{name}</h1>
                  <h3 className="speciality">{company}</h3>
                </div>

                <div className="header-content-links">
                { loading ? <Spinner/> :
                    socialLinkData.map(({ href, iconClass }) => (<SocialLinkIcon key={href} href={href} iconClass={iconClass}/>))
                }
                </div>

                <label className="switch">
                  <input type="checkbox" checked={darkTheme ? true : false} onChange={toggleTheme}/>
                  <span className="slider round"></span>
                </label>

              </header>
            )}
        }
      </ThemeConsumer>
    )
  }
};

const mapStateToProps = ({ generalInformation }) => ({
  socialLinkData: generalInformation.socialLinkData,
  name: generalInformation.name,
  company: generalInformation.company,
  loading: generalInformation.loading,
  error: generalInformation.error
});

export default connect(mapStateToProps, ({ generalInformation, skills, education }) => ({
  generalInformationGetAction: generalInformation.get,
  skillsGetAction: skills.get,
  educationGetAction: education.get
}))(Header);

Header.propTypes = {
  socialLinksGetAction: PropTypes.func,
  skillsGetAction: PropTypes.func,
  educationGetAction: PropTypes.func,
  socialLinkData: PropTypes.array,
  name: PropTypes.string,
  company: PropTypes.string,
  loading: PropTypes.bool,
  error: PropTypes.string
};
