import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { skillsGetAction } from '../../actions/skills';
import SkillProgress from '../../components/skillProgress';
import Card from '../../components/card';
import Spinner from '../../components/spinner';

import './style.css';

class Main extends Component {
  async componentDidMount() {
    await this.props.skillsGetAction();
  }

  render() {
    const { skillProgressData, text, loading, error } = this.props;

    return (
      <Card>
        <div className="skills-card">
          <div className="skills-card-description">
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Praesentium, consequuntur enim? Sit voluptas natus ut est rerum assumenda ratione asperiores voluptatibus molestias commodi incidunt inventore rem, perferendis consequatur? Ipsum, beatae.</p>

            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Est eligendi provident dolor delectus, nemo itaque facilis eveniet. Animi, id tenetur quos, nam doloremque similique, nulla dignissimos placeat optio ullam ipsam.</p>
          </div>
          <div className="skills-progress">
            {
              skillProgressData.map(({ name, percent }) => (<SkillProgress key={name} name={name} percent={percent}/>))
            }
          </div>
        </div>
      </Card>
    )
  }
};

const mapStateToProps = ({ skills }) => ({
  skillProgressData: skills.skills,
  text: skills.text,
  loading: skills.loading,
  error: skills.error
});

export default connect(mapStateToProps, {
  skills
})(Main);

Main.propTypes = {
  skillProgressData: PropTypes.func,
  text: PropTypes.string,
  loading: PropTypes.bool,
  error: PropTypes.bool
};
