import * as types from '../constants/actionTypes';
import { getAccessToken } from '../services/backend';
import config from '../config';

export const tokenGetAction = (code) => async (dispatch) => {
  dispatch({
    type: types.TOKEN_GET
  });

  try {
    const { client_id, client_secret } = config.github;
    const { data } = await getAccessToken({
      client_id,
      client_secret,
      code,
      // redirect_uri: '/login'
    });
    console.log(data);

    return dispatch({
      type: types.TOKEN_GET_SUCCEED,
      data: data
    });
  } catch(err) {
    return dispatch({
      type: types.TOKEN_GET_FAILED
    });
  }
};
