import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { skillsGetAction } from '../../actions/skills';
import SkillProgress from '../../components/skillProgress';
import Card from '../../components/card';
import Spinner from '../../components/spinner';

import './style.css';

class Main extends Component {
  async componentDidMount() {
    await this.props.skillsGetAction();
  }

  render() {
    const { skills, text, loading, error } = this.props;

    return (
      <Card>
        <div className="skills-card">
          { loading ? <Spinner/> :
            <>
              <div className="skills-card-description">
              <p>{text}</p>
              </div>
              <div className="skills-progress">
                {
                  skillProgressData.map(({ name, percent }) => (<SkillProgress key={name} name={name} percent={percent}/>))
                }
              </div>
            </>
          }
        </div>
      </Card>
    )
  }
};

const mapStateToProps = ({ skills }) => ({
  skillProgressData: skills.skills,
  text: skills.text,
  loading: skills.loading,
  error: skills.error
});

export default connect(mapStateToProps, {
  skills
})(Main);

Main.propTypes = {
  skills: PropTypes.func,
  informationData: PropTypes.array,
  text: PropTypes.string,
  loading: PropTypes.bool,
  error: PropTypes.bool
};
