import React, { Component } from 'react';
import { getAccessToken } from '../../services/backend';
import config from '../../config';
import Spinner from '../../components/spinner';

import './style.css';

class GetToken extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      error: null
    }
  }
  async componentDidMount() {
    const queryParams = new URLSearchParams(this.props.location.search); 
    const code = queryParams.get('code')

    const { client_id, client_secret } = config.github;
    const { data } = await getAccessToken({
      client_id, client_secret, code
    });

    console.log(window.location)


    if (data.indexOf('access_token=') === -1) {
      this.setState({
        loading: false,
        error: data
      });
    }
    else {
      const access_token = data.substring('access_token='.length, data.indexOf('&scope='));
      localStorage.setItem('token', access_token);
      localStorage.setItem('isTokenPresented', true);
    }
  }

  render() {
    const { loading, error } = this.state;

    return (
      <>
        { loading ? <Spinner className="token-spinner"/> :
            error ? <div className="token-error">Ooops... ERROR <span>{error}</span></div> :
              window.location.pathname.replace('/resume')
        }
      </>
    )
  }
};

export default GetToken;
