import React, { Component } from 'react';
import {
  HashRouter, Route, Switch, Redirect,
} from 'react-router-dom';
import Header from '../../containers/header';
import Main from '../main';
import LoginPage from '../../containers/loginPage'

import './style.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isTokenPresented: localStorage.getItem('isLoggedIn') === 'true',
      // isTokenChecked: false,

      // error: null,
    };
  }

  render() {
    const { isTokenPresented } = this.state;

    const LoginRoute = () => {
      <Route render={() => (
        !isTokenPresented
          ? <Route path='/login' component={LoginPage}/>
          : <Redirect to='/resume'/>)}/>
    }

    // return (
    //   <>
    //     <Header/>
    //     <Main />
    //   </>
    // )

    return (
      <LoginRoute/>
    )

  }
};

export default App;
