import React, { Component } from 'react';
import {
  BrowserRouter, Route, Switch, Redirect,
} from 'react-router-dom';
import Header from '../../containers/header';
import Main from '../main';
import LoginPage from '../../containers/loginPage';
import GetToken from '../../containers/getToken';

import './style.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isTokenPresented: localStorage.getItem('isTokenPresented') === 'true',
    };
  }

  async componentDidMount() {
    console.log(localStorage.getItem('isTokenPresented'));
  }

  render() {
    const { isTokenPresented } = this.state;

    const Resume = () => (
      <>
        <Header/>
        <Main />
      </>
    );

    const LoginRoute = () => (
      <Route render={() => (
        !isTokenPresented
          ? <Route path='/login' component={LoginPage}/>
          : <Redirect to='/resume'/>
        )}
      />
    );

    const ResumeRoute = ({ component: Component, ...rest }) => (
      <Route {...rest} render={() => (
        isTokenPresented
          ? <Route path='/resume' component={Resume}/>
          : <Redirect to='/login'/>
      )}
      />
    );

    const PrivateRoute = () => (
      <Route render={() => (
        isTokenPresented
          ? <Redirect to='/resume'/>
          : <Redirect to='/login'/>
      )}
      />
    );

    return (
      <BrowserRouter>
        <Switch>
          <Route path='/token' component={GetToken}/>
          <LoginRoute path='/login'/>
          <ResumeRoute path='/resume'/>
          <PrivateRoute/>
        </Switch>
      </BrowserRouter>
    )
  }
};

export default App;
