import { userInfo } from '../services/backend';

export default {
  state: {
    socialLinkData: [],
    name: null,
    company: null,
    loading: true,
    error: null
  },

  reducers: {
    fetching: (state, payload) => ({
      socialLinkData: [],
      name: null,
      company: null,
      loading: true,
      error: null
    }),
    succeed: (state, payload) => ({
      ...state,
      socialLinkData: [
        {
          href: payload.html_url,
          iconClass: 'fa-github'
        },
        {
          href: payload.twitter_username ? `https://twitter.com/${payload.twitter_username}` : null,
          iconClass: 'fa-twitter'
        }
      ],
      name: payload.name,
      company: payload.company,
      loading: false
    }), 
    failed: (state, error) => ({
      ...state,
      loading: false,
      error
    }) 
  },

  effects: {
    async tokenGet(payload, rootState) {
      try {
        this.fetching();
        const { data } = await userInfo();
        this.succeed(data);
      } catch(err) {
        this.failed(err);
      }
    }
  }
}
