import { userInfo } from '../services/backend';

export default {
  state: {
    github: null,
    twitter: null,
    name: null,
    company: null,
    loading: true,
    error: null
  },

  reducers: {
    fetching: (state, payload) => ({
      github: null,
      twitter: null,
      name: null,
      company: null,
      loading: true,
      error: null
    }),
    succeed: (state, payload) => ({
      ...state,
      github: payload.html_url,
      twitter: payload.twitter_username ? `https://twitter.com/${payload.twitter_username}` : null,
      name: payload.name,
      company: payload.company,
      loading: false
    }), 
    failed: (state, error) => ({
      ...state,
      loading: false,
      error
    }) 
  },

  effects: {
    async tokenGet(payload, rootState) {
      try {
        this.fetching();
        const { data } = await userInfo();
        this.succeed(data);
      } catch(err) {
        this.failed(err);
      }
    }
  }
}
