import React, { Component }  from 'react';
import SocialLinkIcon from '../../components/socialLinkIcon';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Spinner from '../../components/spinner';

import './style.scss';

class Header extends Component {

  async componentDidMount() {
    await this.props.socialLinksGetAction();
  }

  render() {
    const { socialLinkData, loading, name, company } = this.props;

    return (
      <header>
        <div className="header-content-data">
          <h1 className="person-name">{name}</h1>
          <h3 className="speciality">{company}</h3>
        </div>
        <div className="header-content-links">
        { loading ? <Spinner/> :
            socialLinkData.map(({ href, iconClass }) => (<SocialLinkIcon key={href} href={href} iconClass={iconClass}/>))
        }
        </div>
      </header>
    )
  }
};

const mapStateToProps = ({ socialLinks }) => ({
  socialLinkData: socialLinks.socialLinkData,
  name: socialLinks.name,
  company: socialLinks.company,
  loading: socialLinks.loading,
  error: socialLinks.error
});

export default connect(mapStateToProps, ({ socialLinks }) => ({
  socialLinksGetAction: socialLinks.get
}))(Header);

Header.propTypes = {
  socialLinksGetAction: PropTypes.func,
  socialLinkData: PropTypes.array,
  name: PropTypes.string,
  company: PropTypes.string,
  loading: PropTypes.bool,
  error: PropTypes.string
};
