import React from 'react';

const themes = {
  light: 'light',
  dark: 'dark'
}

const {
  Provider: ThemeProvider,
  Consumer: ThemeConsumer
} = React.createContext();

export {
  ThemeProvider,
  ThemeConsumer,
  themes
};
