import {
  SOCIALLINKS_GET, SOCIALLINKS_GET_SUCCEED, SOCIALLINKS_GET_FAILED
} from '../constants/actionTypes';

const initialState = {
  socialLinkData: []
};

export default function generalInformation(state = initialState, action) {
  switch (action.type) {
    case SOCIALLINKS_GET:
      return {
        ...state,
        socialLinkData: [],
        loading: true,
        error: false,
      };
    case SOCIALLINKS_GET_SUCCEED:
      return {
        ...state,
        socialLinkData: [
          action.github ? {
            href: action.github,
            iconClass: 'fa-github'
          } : undefined,
          action.twitter ? {
            href: action.twitter,
            iconClass: 'fa-twitter'
          } : undefined
        ],
        loading: false,
        error: false,
      };
    case SOCIALLINKS_GET_FAILED:
      return {
        ...state,
        loading: false,
        error: true,
      };
    default:
      return state;
  }
}
