import { userInfo } from '../services/backend';

export default {
  state: {
    informationData: {},
    name: null,
    position: null,
    avatar: null,
    loading: true,
    error: null
  },

  reducers: {
    fetching: (state, payload) => ({
      informationData: {},
      name: null,
      position: null,
      avatar: null,
      loading: true,
      error: null
    }),
    succeed: (state, payload) => ({
      ...state,
      informationData: {
        company: payload.company,
        blog: payload.blog,
        location: payload.location,
        email: payload.email,
        bio: payload.bio
      },
      name: payload.name,
      position: payload.company,
      avatar: payload.avatar_url,
      loading: false
    }), 
    failed: (state, error) => ({
      ...state,
      loading: false,
      error: JSON.stringify(error)
    }) 
  },

  effects: {
    async get(payload, rootState) {
      try {
        this.fetching();
        const { data } = await userInfo(payload);
        this.succeed(data);
      } catch(err) {
        this.failed(err);
      }
    }
  }
}
